property.value:
var payload= mc.getPayloadJSON();
var smsRetrieveResourceUrlPrefix = mc.getProperty('SEND_SMS_RESOURCE_URL_PREFIX').trim();
var requestId = mc.getProperty('REQUEST_ID').trim();
var encodedSenderAddress = mc.getProperty('ENCODED_SENDER_ADDRESS').trim();
var request = '/request/';
var smsRetrieveResourceUrl = String(smsRetrieveResourceUrlPrefix +request +requestId);

var responseResourceUrl = payload.outboundSMSMessageRequest.resourceURL;
var operatorRequestId = responseResourceUrl.substring(responseResourceUrl.lastIndexOf('/') + 1);
if (!operatorRequestId) {
    throw 'ERROR - Unable extract operator request id (plugin_requestid) from response';
}
mc.setProperty('SEND_SMS_OPERATOR_REQUEST_ID', operatorRequestId);

var responseDeliveryInfoResourceUrl = mc.getProperty('RESPONSE_DELIVERY_INFO_RESOURCE_URL');
if (mc.getProperty('OPERATOR_DELIVERY_INFO_RESOURCE_URL') == 'true') {
    responseDeliveryInfoResourceUrl = payload.outboundSMSMessageRequest.deliveryInfoList.resourceURL
}
payload.outboundSMSMessageRequest.deliveryInfoList.resourceURL = responseDeliveryInfoResourceUrl;
payload.outboundSMSMessageRequest.resourceURL=smsRetrieveResourceUrl;
mc.setPayloadJSON(payload);
